import React from 'react'

import './css/components.css'


/*******************************************************************************
Components
*******************************************************************************/

let updateChoice;

export function Audio({ name, file }) {
	return (
		<audio className='audio-single' controls>
			<source src={`user-studies/${name}/audio/${file}`} type='audio/mpeg'/>
		</audio>);
};

export function Choice({ key, index, choice, setChoice, label }) {
	console.log(choice);
	return (
		<li className="grid">
			<input type="radio" id={key} />
			<div className={`check col-1 ${index === choice ? "selected" : ""}`} onClick={() => setChoice(index)}>
				<div className="inside"></div>
			</div>
			<label className="col-3">{label}</label>
		</li>
	);
};

export function MultipleChoice({ index, choice, setChoice, labels }) {
	updateChoice = setChoice;
	const multipleChoice = labels.map((item, key) =>
		<Choice 
			key={key}
			index={index}
			choice={choice}
			setChoice={setChoice}
			label={item}
		/>
	);
	return (
		<ul className="MultipleChoice">
			{multipleChoice}
		</ul>
	);
};

export {updateChoice};
